import requests from bs4 import BeautifulSoup from selenium import webdriver from selenium.webdriver.common.keys import Keys import time   #url of the page we want to scrape url = "https://www.immoweb.be/en/search/house/for-sale?countries=BE&page=1&orderBy=relevance"  # initiating the webdriver. Parameter includes the path of the webdriver. PATH = "/usr/local/bin/chromedriver"driver = webdriver.Chrome(PATH)driver.delete_all_cookies()# this is just to ensure that the page is loaded time.sleep(5)    html = driver.page_source   # this renders the JS code and stores all # of the information in static HTML code.   # Now, we could simply apply bs4 to html variable soup = BeautifulSoup(html, "html.parser") links_html = soup.find_all('a', class_='card__title-link')for link in links_html:    linkingsite=link.get('href')for link in linkingsite:    url = "str(link)"     PATH = "/usr/local/bin/chromedriver"    driver = webdriver.Chrome(PATH)    driver.get(url)         driver.delete_all_cookies()  #print(links_html)